import React,{ Component} from 'react';
import { Animate } from "react-move";
import Constants from '../Constants';
import Symbol from './Symbol';

class Reel extends Component{
    constructor(props){
        super(props);

        this.state={
            width:Constants.REEL_WIDTH,
            height:Constants.REEL_HEIGHT
        }

        this.symbol="AAGGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQDDD";
    }
    render(){
        return( 
            <div className="reel">
                <div className="reelSymbol">
                    {this.symbol.split("").map((el, idx)=>{
                        return <Symbol symbol={el} key={idx }index={idx}/>
                    })}
                </div>
            </div>
        )
    }
}

export default Reel;
