import React, { useState } from "react";
import ReelSet from "./components/ReelSet";
import Bet from "./components/Bet";

function App() {
  const [gameOn, setGameOn] = useState(0);
  const [rolling, setRolling] = useState(false);

  const [score, setScore] = useState(2);

  const handleSpin = () => {
    setGameOn(Math.floor(Math.random() * 42 + 1));
  };

  return (
    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
      <ReelSet startGame={gameOn} />
      <div id="controls">
        <div
          className={!rolling ? "roll rolling" : "roll"}
          onClick={() => {
            handleSpin();
          }}
          disabled={rolling}
        >
          {rolling ? "Rolling..." : "ROLL"}
        </div>
        <div
          //   className={!withdrawal ? "roll rolling" : "roll"}
          onClick={() => {}}
          //  disabled={withdrawal}
        ></div>
        <Bet score={score} />
        <label>
          Score:<span id="score">2</span>
        </label>
      </div>
    </>
  );
}

export default App;
