// let getReels="";

 
 const callbackReelLightsOff = (something)=>{
    if(props.gameOn<0){
      reelOpacity=0.3
      return reelOpacity
    }
    return reelOpacity;
 }

 reelOpacity=1;

 const variants = {
    hidden: {
      opacity: 0.3,
      transition:{
        delay: 2
    }
    }
   }

 if(props.gameOn<0){
    controls.set({ opacity: 0.3 }) ;
 }
 
 return (
    <motion.div animate={controls} className="reel">{reelSymbols.map((el, idx) => {
        return <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn} callbackSomethign={callbackReelLightsOff}/>;
    })} 
    </motion.div>);
};
export default Reel;


