import React, { useRef,useEffect, useState } from "react";
import "../assets/css/App.css";
import images from "../assets/images/ImagesExplained";
import {motion} from "framer-motion";


const GameExplained = () => {

    const [width, setWidth] = useState(0);
    const carousel = useRef();

    useEffect(()=>{
        console.log("From carousel ! = > "+carousel.current.scrollWidth, carousel.current.offsetWidth);
        setWidth(carousel.current.scrollWidth - carousel.current.offsetWidth);
    },[]);


    return(
        <>
        <div className="winningPatterns">
            <h2 className="h2-title">Winning Patterns and Values</h2>
        </div>
       
        </>
    )
}

export default GameExplained;
