import React, { useEffect, useRef} from "react";
import Image from "../assets/images/Images";
import {motion,animate,Variants} from "framer-motion";



const Symbol = (props, ref)=> {
  let image = "";
  const [isOpen, setIsOpen] = useState(false);
 
  const getImage =  () => {
    if(props.spin<0){
    switch (props.symbol) {
      
      case "A":
        return Image.America;
      case "G":
        return Image.Aragon;
      case "N":
        return Image.Argentina;
      case "S":
        return Image.Assen;
      case "U":
        return Image.Australia;
      case "C":
        return Image.Catalunya;
      case "F":
        return Image.Finland;
      case "R":
        return Image.France;
      case "Y":
        return Image.Germany;
      case "I":
        return Image.Indonesia;
      case "J":
        return Image.Japan;
      case "Z":
        return Image.Jerez;
      case "M":
        return Image.Malaysia;
      case "O":
        return Image.Misano;
      case "L":
        return Image.Mugello;
      case "T":
        return Image.Osterreich;
      case "P":
        return Image.Portugal;
      case "Q":
        return Image.Qatar;
      case "H":
        return Image.Thailand;
      case "K":
        return Image.UK;
      case "D":
        return Image.Fool;
      case "V":
        return Image.Valencia;
      default:
        return Image.Fool;
    }
  }else{
    return Image.Fool;
  }
  };

    image = getImage();

    const variants = {
      open: { y: props.spin, opacity: props.opacity },
      closed: { opacity: 0, x: "-100%" },
    }
   

    const spinReel =()=>{
      return (
        
        <div className="icons" >
          <motion.img         
                      animate={ isOpen ? "open" : "closed" }
                      transition={{
                        type: 'tween',
                        duration: 1.5,
                        ease: 'easeInOut'
                      }} 
                      variants={variants}
                      initial="hidden"
                      
                    src={image} alt=""/>
                  </div>
                   
      );
  }

    return (
      <>{spinReel()}</>
    );

};

export default Symbol;

