import React, {useState} from 'react';
import Constants from '../Constants';
import Symbol from './Symbol';



const Reel = (props) =>{
    //42 symbols
    const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
    const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");
//    const [reelEffect,setReelEffect] = useState(0);
    console.log("My props.st: "+props.gameOn);
    const getRandomNr=()=>{
        let randomNr = 0;
        if(props.gameOn>0){
            randomNr = -(Math.floor((Math.random() * 42)+1) *242);
        }
        const retNr=randomNr;
        randomNr=0;
        return retNr;
    }
    const renderImages = () =>{
        let imagesList = reelSymbols.map((el, idx)=>
            {return <Symbol symbol={el} key={idx} index={idx} spin={getRandomNr()} />
         })
         return <>{imagesList}</>
        }

    return(
        
        <div className="reel" >
            {renderImages()}
        </div>
    )
}
export default Reel;
