import React, { useRef,forwardRef} from "react";
import Image from "../assets/images/Images";
import {motion,animate,transform} from "framer-motion";


const Symbol = forwardRef((props,ref)=> {
  let image = useRef("");

  const getImage = () => {
    if(props.spin<0){
    switch (props.symbol) {

      case "A":
        return Image.America;
      case "G":
        return Image.Aragon;
      case "N":
        return Image.Argentina;
      case "S":
        return Image.Assen;
      case "U":
        return Image.Australia;
      case "C":
        return Image.Catalunya;
      case "F":
        return Image.Finland;
      case "R":
        return Image.France;
      case "Y":
        return Image.Germany;
      case "I":
        return Image.Indonesia;
      case "J":
        return Image.Japan;
      case "Z":
        return Image.Jerez;
      case "M":
        return Image.Malaysia;
      case "O":
        return Image.Misano;
      case "L":
        return Image.Mugello;
      case "T":
        return Image.Osterreich;
      case "P":
        return Image.Portugal;
      case "Q":
        return Image.Qatar;
      case "H":
        return Image.Thailand;
      case "K":
        return Image.UK;
      case "D":
        return Image.Fool;
      case "B":
        return Image.Valencia;
      default:
        return Image.Fool;
    }
  }else{
    return Image.Fool;
  }
  };

  const renderImages=()=>{
    image = getImage();
    return (
        <div className="icons">
            <motion.img animate={{ y: props.spin, opacity: 1 }}
                        transition={{
                            delay: 0.3,
                            x: { stiffness: 50, damping:3 },
                      default: { duration: 0.5 }}} src={image} alt="" ref={image}/>
        </div>
    )
  }


    return (
      <>
       {renderImages()}
      </>
    );

});

export default Symbol;



