import  React, { useEffect, useState, useRef } from "react";
import '../assets/css/App.css';
import {ethers} from "ethers";
import PFPRacerNFT from '../artifacts/contracts/PFPRacerNFT.sol/abi.json';

const Mint = (props) =>{
    const [mintAmount, setMintAmount] = useState(1);
    const [leftToMint, setLeftToMint] = useState(0);
    const [contract, setContract] = useState("");
    const [account, setAccount] = useState("");
    

    const initConnection = async () =>{
        if(typeof window.ethereum !== "undefined"){
          const accounts = await window.ethereum.request({
            method: "eth_requestAccounts",
          });
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const newSigner = provider.getSigner();
          setAccount(accounts[0]);
          setContract(
            new ethers.Contract(
              "0xEE8E55e5BE8fAB0aa31D23eeb0F976baB4C2E557",
              PFPRacerNFT.abi,
              newSigner
            )
          )
        }else{
          console.log("Please install metamask");
        }
      }

    const howManyNFTsAreLeft = async () =>{
        let totalNFT = await contract.maxSupply();
        let mintedNFT= await contract.getNFTBalance(); //to be changed into getNFTBalance
        console.log("My totalNFT's in howManyNFTsAreLeft is : "+totalNFT);
        console.log("My total mintedNFT is"+mintedNFT);
        let nftLeftToMint = totalNFT - mintedNFT;
        setLeftToMint(nftLeftToMint);
        console.log("Total NFT's left to mint "+nftLeftToMint);
      }

    useEffect(()=>{
        initConnection();
        howManyNFTsAreLeft();
      },[account]);


const handleSubmit= async(amount) =>{
    
    console.log("Minting amount: "+amount);
    await contract.mint(amount);
   // howManyNFTsAreLeft();
    alert(`We have minted ${amount}`)
}
      return (
        <>
        <form class="form" onSubmit={handleSubmit()}>
        <label class="label-mint"> 
             NFT's left to mint:
             <p class="p-welcomeMint">{leftToMint}</p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        </label>
          <label class="form-label">
            Mint Amount:
            <input
              type="text"
              value={mintAmount}
              onChange={e => setMintAmount(e.target.value)}
            />
          </label>
          <input class="form-submit-button" type="submit" value="Mint" />
        </form>
        </>
    
      );
}



export default Mint;
