import React from "react";
import ReactDOM from "react-dom";
import "../assets/css/App.css";
import ReactSimpleCarousel from "react-spring-carousel";
import  images from "../assets/images/ImagesExplained";

const GameScoreCarousels = () =>{

    const [currentIndex, setCurrentIndex] = useState(0);
    const carouselInfiniteScroll = () =>{
        if(currentIndex===images.length-1){
            return setCurrentIndex(0);
        }
        return setCurrentIndex(currentIndex+1);
    }

    useEffect(()=> {
        const interval = setInterval(()=> {carouselInfiniteScroll()}, 3000);
        return () => clearInterval(interval)
    })

    return (
    <>
        <div className="winningPatterns">
            <h2 className="h2-title">Winning Patterns and Values</h2>
        </div>
        <div className="carousel">
        <ReactSimpleCarousel slidesToShow={5}>
          {images.map(({ image, text }, i) => (
            <div key={i} className="App-slide">
              <a href={`#${i}`} draggable="false">
                <img src={image} alt="" />
              </a>
              <p
                style={{
                  position: "absolute",
                  top: "50%",
                  left: "50%",
                  transform: "translateX(-50%) translateY(-50%)"
                }}
              >
                {text}
              </p>
            </div>
          ))}
        </ReactSimpleCarousel>
      </div>
    </>
        )
}

export default GameScoreCarousels;
