import React, { useEffect, useRef, forwardRef} from "react";
import Image from "../assets/images/Images";
import {motion,animate,variants} from "framer-motion";


const Symbol = (props)=> {
  let image = "";
  let imageRef=  useRef();
 
  const getImage =  () => {
    if(props.spin>0){
    switch (props.symbol) {
      
      case "A":
        return Image.America;
      case "G":
        return Image.Aragon;
      case "N":
        return Image.Argentina;
      case "S":
        return Image.Assen;
      case "U":
        return Image.Australia;
      case "C":
        return Image.Catalunya;
      case "F":
        return Image.Finland;
      case "R":
        return Image.France;
      case "Y":
        return Image.Germany;
      case "I":
        return Image.Indonesia;
      case "J":
        return Image.Japan;
      case "Z":
        return Image.Jerez;
      case "M":
        return Image.Malaysia;
      case "O":
        return Image.Misano;
      case "L":
        return Image.Mugello;
      case "T":
        return Image.Osterreich;
      case "P":
        return Image.Portugal;
      case "Q":
        return Image.Qatar;
      case "H":
        return Image.Thailand;
      case "K":
        return Image.UK;
      case "D":
        return Image.Fool;
      case "V":
        return Image.Valencia;
      default:
        return Image.Fool;
    }
  }else{
    return Image.Fool;
  }
  };

    image = getImage();

    const variants = {
      opacity:{
        opacity:1
      }
    }

    return (
      
            <motion.img 
                        variants={variants} animate="opacity" src={image} alt=""  ref={imageRef} />
    );

};

export default Symbol;



