import React, { useEffect, useState} from "react";
import Constants from "../Constants";
import Reel from "./Reel";
import "../assets/css/App.css";

const ReelSet =(props) =>{
	
	const [width, setWidth] = useState();
	const [height, setHeight] = useState();
	const [bet, setBet] = useState();
	const [score, setScore]= useState();
	
	const reels = [];
	const reelsInMotion = null;
    const spinResults = [];
    const winningLines = [];
    const strike = false;
    const LINES = Constants.LINES;
    const scr = 2;
    const countWin = 0;
    const symbolSellection = [];
	
  const randomBetween = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
  };

  const doSomething = (reelIndex, results) => {
    reelsInMotion -= 1;
    spinResults[reelIndex] = results;

    if (reelsInMotion === 0) {
   //   checkWin();
      console.log("We have finished rolling");
    //  setScoreState();
    }
  }
	
  const spin =() => {
    // winningLines = [];
     const reelsInMotion = Constants.REELS;
     for (let i = 0; i < Constants.REELS; i++) {
       reels[i].scrollByOffSet(
         randomBetween(
           (Constants.REELS_REPEAT - 6) * reels[i].symbols.length,
           (Constants.REELS_REPEAT - 5) * reels[i].symbols.length
         ),
         (reelIndex, results) => {
          this.reelsInMotion -= 1;
          this.spinResults[reelIndex] = results;

          if (this.reelsInMotion === 0) {
            this.checkWin();
            console.log("We have finished rolling");
          //  this.setScoreState();
          }
        }
       );
     }
   };
	
	const onLayout = (e) => {
		setWidth(1200);
		setHeight(800);
	};

	const renderReels = () => {
		let reelWidth = width / Constants.REELS;
		console.log("My reelWidth is: "+ reelWidth);
		console.log("My hight is: "+height);
		let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
		  return (
			<Reel
			  width={reelWidth}
			  height={height}
			  key={idx}
			  index={idx}
			  ref={(ref) => {
				reels[idx] = ref;
			  }}
			/>
		  );
    });

    return <>{reelList}</>;
  };
	
	

}

export default ReelSet;
