import React, {Fragment,useState} from 'react';
import Constants from '../Constants';
import Symbol from './Symbol';
import SlotMachine from 'react-slot-machine-gen';

const Reel = (props) =>{

    const symbols = "AAGGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQDDD";
    const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");

    const [play, setPlay] = useState(true);

    const playEvent=()=>{
        setPlay(true);
    }

    return(
        <Fragment>
        <div className="reel" >
          <SlotMachine reels={reelSymbols.map((el, idx)=>
                {return <Symbol symbol={el} key={idx} index={idx} />
            })} play={true}
            > </SlotMachine> 
        </div>
        </Fragment>
    )
}
export default Reel;
