import React,{ Component} from 'react';
import { Animate } from "react-move";
import Constants from '../Constants';
import Symbol from './Symbol';

class Reel extends Component{
    constructor(props){
        super(props);
        this.symbols=Constants.REEL_SYMBOLS[this.props.index];
        this.reelSymbols = this.symbols.repeat(Constants.REELS_REPEAT).split("");
        this.symbolHeight = this.props.height/ Constants.SYMBOLS;

        this.symbolsRefs = [];

        this.position = this.reelSymbols.length - Constants.SYMBOLS;
        this.currentScrollPos=this.position * this.symbolHeight * -1;
        this.state = {
            scrollPos: new Animate.Value(this.currentScrollPos)
        }
    }

    highLightAtIndex = (index, highLight) => {
        this.symbolsRefs[this.position + index].setActive(highLight);
    }

    shakeAtIndex = (index) => {
        this.symbolsRefs[this.position + index].shake();
    }

    scrollByOffSet = (offset, callback) => {
        console.log("We are in: scrollByOffSet !!!");
        for(let i=0; i<Constants.SYMBOLS; i++){
            this.symbolsRefs[this.position+i].setActive(true);
    }

        this.currentScrollPos = this.currentScrollPos + (this.symbolHeight * offset);
        this.position = this.position - offset;
        Animate.timing(
            this.state.scrollPos,
            {
                toValue:this.currentScrollPos,
                duration: 750 + (this.props.index * 250),
                useNativeDriver: true,
                easing: Animate.easing
              //  easing: Easing.inOut(Easing.exp)

            }
            
        ).start(()=>{
            this.position = ((Constants.REELS_REPEAT - 2) * this.symbols.length ) + (this.position % this.symbols.length);
            let results = [];
            for(let i=0; i<Constants.SYMBOLS; i++){
                this.symbolsRefs[this.position+i].setActive(false);
                results.push(this.reelSymbols[this.position+i]);
            }

            this.currentScrollPos = this.position * this.symbolHeight * -1;
            this.state.scrollPos.setValue(this.currentScrollPos);

            callback(this.props.index, results);
        });
    }

    render(){
        const reel= {
            overflow: 'hidden'
        }
        return(
            <div style={[reel,{width: this.props.width, height: this.props.height}]} >
               <Animate style={{ width:this.props.width, height:this.reelSymbols.length * this.symbolHeight, transform:[{ translateY: this.state.scrollPos }] }}>
                   {this.reelSymbols.map((el, idx)=>{
                       return <Symbol symbol={el} key={idx} index={idx} width={this.props.width} height={this.symbolHeight} ref={(ref) => { this.symbolsRefs[idx]=ref; }}/>
                   })}
               </Animate>
            </div>
        );
    }
}

export default Reel;
