import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import {motion} from 'framer-motion';

let opacity =1;

const Reel = (props) => {
  //42 symbols
//  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPKKQQAAG";
//const symbols = "DDDGNNSS";
  let reelRef= useRef();
  const symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";


  if(props.opacity <1){
    console.log("My opacity from Reel is:"+props.opacity)
    opacity=props.opacity;
  }

  useEffect(()=>{
    console.log("My opacity useEffect is: "+opacity);
  },[opacity])

  if(props.gameOn===0){
      reelSymbols = symbols[props.index].split("");
  }else{
    reelSymbols = symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
  }  
// console.log("My number is: "+(-(props.gameOn/242)));
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))]);
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+1]);
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+2]);
// let getReels="";



const setupReel= ()=>{
  return (
  <motion.div animate={{delay:3000}} className="reel">
    {reelSymbols.map((el, idx) => {
    return  <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn} opacity={opacity} ref={reelRef} blur={1}/>;
      })} 
  </motion.div>
);
}

  return (
      <>{setupReel()}</>
    );
};
export default Reel;


