                    </div>
                )
            }, 
            {
                id: image7,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image7} alt=""/>
                    </div>
                )
            }, 
            {
                id: image8,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image8} alt=""/>
                    </div>
                )
            },
            {
                id: image9,
                renderItem: (
                    <div className="item">
