                    </div>
                )
            },
            {
                id: image10,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image10} alt=""/>
                    </div>
                )
            },
            {
                id: image11,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image11} alt=""/>
                    </div>
                )
            },
            {
                id: image12,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image12} alt=""/>
                    </div>
                )
            },
            {
                id: image13,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image13} alt=""/>
                    </div>
                )
            },
            {
                id: image14,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image14} alt=""/>
                    </div>
                )
            },
            {
                id: image15,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image15} alt=""/>
                    </div>
                )
            }, {
                id: image16,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image16} alt=""/>
                    </div>
                )
            }, {
                id: image17,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image17} alt=""/>
                    </div>
                )
            },
            {
                id: image18,
                renderItem: (
                    <div className="item">
                        <img className="myImage" src={image18} alt=""/>
                    </div>
                )
            }
        ];

        const { 
            carouselFragment, 
            slideToPrevItem, 
            slideToNextItem 
          } = useSpringCarousel({
            itemsPerSlide: 3, 
            withLoop: true,
            items: items.map((i) => ({
              id: i.id,
              renderItem: (
                <div  className="item">
                    <img className="myImage" src={i.id} alt=""/>
                </div>
              ),
            })),
          });

    return (
    <>
        <div className="winningPatterns">
            <h2 className="h2-title">Winning Patterns and Values</h2>
        </div>
        <div className="carousel">
            <div className="inner-carousel" >
                {carouselFragment}
            </div>
      </div>
    </>
        )
}

export default GameScoreCarousels;
