import React, { useState} from "react";
import Image from "../assets/images/Images";
import {motion,animate,transform} from "framer-motion";

const variants = {
  rotate: { rotate: [0, -30, 0], transition: { duration: 0.5 } },
  stop: { y: [0, -10, 0], transition: { repeat: Infinity, repeatDelay: 3 } }
};

const Symbol =(props)=> {

  const [symb, setSymb] = useState(null);

  const setImage =() =>{
    setSymb(getImage());
  }

  const getImage = () => {
    switch (props.symbol) {
      case "A":
        return Image.America;
      case "G":
        return Image.Aragon;
      case "N":
        return Image.Argentina;
      case "S":
        return Image.Assen;
      case "U":
        return Image.Australia;
      case "C":
        return Image.Catalunya;
      case "F":
        return Image.Finland;
      case "R":
        return Image.France;
      case "Y":
        return Image.Germany;
      case "I":
        return Image.Indonesia;
      case "J":
        return Image.Japan;
      case "Z":
        return Image.Jerez;
      case "M":
        return Image.Malaysia;
      case "O":
        return Image.Misano;
      case "L":
        return Image.Mugello;
      case "T":
        return Image.Osterreich;
      case "P":
        return Image.Portugal;
      case "Q":
        return Image.Qatar;
      case "H":
        return Image.Thailand;
      case "K":
        return Image.UK;
      case "D":
        return Image.Fool;
      case "B":
        return Image.Valencia;
      default:
        return Image.Fool;
    }
  };

  const reelEffect=-1200-(props.index*100);

    return (
        <div className="icons">
            <motion.img animate={{ y: reelEffect, opacity: 1 }}
    transition={{
      delay: 1,
      x: { type: "spring", stiffness: 50 },
      default: { duration: 0.5 },
      repeat: Infinity,}} src={getImage()} alt=""/>
        </div>
    );

}

export default Symbol;
