import React, { useState,useRef, useEffect} from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import { motion } from "framer-motion";

let offset=0;

const Reel = (props) => {

  let symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";
  let reels = [];
  let randomNr=0;

  let symbolRef = useRef();
  let reelRef = useRef([]);

  const getRandomNr = () => {
    if (props.game > 0) {
      randomNr = -(Math.floor(Math.random() * ((symbols[props.index].length-3)+3)) * 246);
    }
    console.log("randomNr = "+randomNr);
    
    if(offset===randomNr){
        getRandomNr();
    }
    else if(offset===0 && props.game>0){
        getRandomNr();
    }
   else{
        offset=randomNr;
    }
  };

  if (props.game === 0) {
    reelSymbols = symbols[props.index].split("");
  } else {
    reelSymbols = symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
    getRandomNr();
  }

  useEffect(() => {
    console.log("My refSymbol is: "+symbolRef.current.childNodes[0]);
  });

  const createReels = () =>{
    reels=reelSymbols.map((el, idx) => {
        return (
          <Symbol symbol={el} key={idx} index={idx} spin={props.game} />
        );
      })
      return <>{reels}</>
  }

  const variants = {
    opacity: {
       y:offset,
       opacity:1, 
       duration: 10,
       delay: 400*props.index
    }
  };

  return (
    <div className="reel" >
      <motion.div animate="opacity" variants={variants} className="icons" ref={symbolRef}>
        {createReels()}
      </motion.div>
    </div>
  );
};
export default Reel;

