import React, { useState} from "react";
import Image from "../assets/images/Images";
import {motion,animate,transform} from "framer-motion";

// Animation properties for the container
// which is the face of the die
const container = {
  hidden: { opacity: 1, scale: 0 },
  visible: {
    scale: [0, 1],
    opacity: 1,
    transition: {
      staggerChildren: 0.5,
      delayChildren: 0.5,
    },
  },
};

const Symbol =(props)=> {

  const [symb, setSymb] = useState(null);

  const setImage =() =>{
    setSymb(getImage());
  }

  const getImage = () => {
    if(props.spin<0){
    switch (props.symbol) {
      case "A":
        return Image.America;
      case "G":
        return Image.Aragon;
      case "N":
        return Image.Argentina;
      case "S":
        return Image.Assen;
      case "U":
        return Image.Australia;
      case "C":
        return Image.Catalunya;
      case "F":
        return Image.Finland;
      case "R":
        return Image.France;
      case "Y":
        return Image.Germany;
      case "I":
        return Image.Indonesia;
      case "J":
        return Image.Japan;
      case "Z":
        return Image.Jerez;
      case "M":
        return Image.Malaysia;
      case "O":
        return Image.Misano;
      case "L":
        return Image.Mugello;
      case "T":
        return Image.Osterreich;
      case "P":
        return Image.Portugal;
      case "Q":
        return Image.Qatar;
      case "H":
        return Image.Thailand;
      case "K":
        return Image.UK;
      case "D":
        return Image.Fool;
      case "B":
        return Image.Valencia;
      default:
        return Image.Fool;
    }
  }else{
    return Image.Fool;
  }
  };

    return (
        <div className="icons">
            <motion.img animate={{ y: props.spin, opacity: 1 }}
                        transition={{
                            delay: 0.3,
                            x: { stiffness: 50, damping:3 },
                      default: { duration: 0.5 }}} src={getImage()} alt=""/>
        </div>
    );

}

export default Symbol;



