import React, { useEffect, useRef, useState } from "react";
import Constants from "../Constants";
import Bet from "./Bet";
import Withdrawal from "./Withdrawal";
import "../assets/css/App.css";
import Reel from "./Reel";

let score = 2;
let bet = 0;

const ReelSet = (props) => {
  //  const [spinReel, setSpinReel] = useState(false)
  const [gameOn, setGameOn] = useState(0);
  const [opacity, setOpacity] = useState(1);

  //  let [bet, setBet] = useState(0)
  //  const [startGame, setStartGa] = useState(2);
  //let gameOn=0;
  const [rolling, setRolling] = useState(false);

  let scoreRef = 0;
  //let score = 2;

  let spinResults = [];
  let results = [];

  //  let bet = 0;

  let winningLines = [];
  let countWin = 0;
  let symbolSellection = [];
  let randomNr = 0;
  //  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
  //const symbols = "DDDGNNSS"
  const symbols = Constants.REEL_SYMBOLS;

  const handleScore = (e) => {
    // /  const [gameScore, setGameScore] = useState({score:0, bet:0})
    score = Math.round((score + e) * 100) / 100;

    //  console.log("mySetGameScore = "+gameScore.score);

    //setGameScore(score);
    // props.dadCallback(score)
  };
  const createArray = (randomNr, index) => {

    for (let i = 0; i < 3; i++) {
      results.push(symbols[index][-(randomNr / 242) + i]);
    }
  };

  const getRandomNr = (index) => {
    if (gameOn > 0) {
      randomNr = -(
        Math.floor(Math.random() * (symbols[index].length - 3) + 3) * 242
      );
      createArray(randomNr, index);
    }
    return randomNr;
  };

  const renderReels = (opacity) => {
    if(opacity===1){
      let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
        return <Reel reel={el} key={idx} index={idx} gameOn={getRandomNr(idx)} opacity={opacity} />;
      });
      randomNr = 0;
      createResults();

      return <>{reelList}</>;
  }else if(opacity<1){
    console.log("We are setting opacity !!!"+opacity);
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} gameOn={getRandomNr(idx)} opacity={opacity} />;
    });

    return <>{reelList}</>;
  }
  };

  const drawScore = (lineNumberType, fool) => {
    let newScore = 0;
    if (fool) {
      lineNumberType = 18;
    }
    switch (lineNumberType) {
      case 0: // top line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 1: // middle line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 2: // bottom line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 3: // V shape starting bottom left
        //        audioWin.play();
        newScore = 0.2;
        break;
      case 4: // V shape starting bottom right
        //        audioWin.play();
        newScore = 0.2;
        break;
      case 5: // W shape starting top left
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 6: // M shape starting bottom left
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 7: // M shape on top half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 8: // W shape on top half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 9: //W shape on bottom half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 10: // M shape on bottom half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 11: // U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 12: // inverse U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 13: // U shape on bottom half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 14: // inverse U shape on bottom half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 15: // inverse U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 16: // Z shape from top left
        //        audioWin.play();
        newScore = 0.4;
        break;
      case 17: // Z shape from bottom left
        //        audioWin.play();
        newScore = 0.4;
        break;
      case 18: // Z shape from bottom left
        //        audioWinBonus.play();
        newScore = 100;
        break;
      default:
        newScore = 0;
    }

    if (gameOn > 0) {
      setMyScore(newScore);
    }
    return;
  };

  const createResults = () => {
    let myResults = [];
    let j = 0;

    for (let i = 0; i <= results.length; i++) {
      myResults[j] = results[i];
      j++;

      if (j === 3) {
        spinResults.push(myResults);
        myResults = [];
        j = 0;
      }
    }

    checkWin();
    // score=score+scr;
    // handleScore();
    // setScore(scr);
  };

  const spinReel = () => {
    if (bet === 0 && score > 0) {
      alert("Please set a bet !!!");
    } else if (bet === 0 && score === 0) {
      alert("Game Over !!! You are you are out of cash !!!");
    } else {
      setGameOn(gameOn + 1);
      
      setOpacity(0.3);
      //  checkWin();
      //  game=1+gameOn;
      // /  console.log("My spinReel game="+game);

      // props.parrentCallBack(score);
    }
  };

  useEffect(() => {
    console.log("useEffect my opacity is set to:" + opacity);
  }, [opacity]);

  const callbackBet = (e) => {
    //bet=e;
    //setBet(()=>({bet:e}));

    bet = e;
  };

  const checkLinesElements = (symbol) => {
    countWin++;
    let symNr = 0;
    let fool = 0;
    for (let i = 0; i < Constants.LINES.length; i++) {
      fool = 0;
      symNr = 0;
      for (let j = 0; j < Constants.LINES[i].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];

        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
        if (symbol === spinResults[indexI][indexJ]) {
          symNr++;
        }
      }
      if (fool + symNr === 5 && fool < 4) {
        winningLines.push(i);
      }
    }
    winningLines = uniq(winningLines);
  };

  const uniq = (a) => {
    return a.sort().filter(function (item, pos, ary) {
      return !pos || item !== ary[pos - 1];
    });
  };

  const checkElements = () => {
    let fool = 0;
    for (let k = 0; k < 3; k++) {
      fool = 0;
      for (let i = 0; i < spinResults.length; i++) {
        let symb = "";
        for (let j = k; j < spinResults[i].length; j++) {
          if (spinResults[i][j] !== "D") {
            symb = spinResults[i][j];
            j = spinResults[i].length;
          } else {
            fool++;
            j = spinResults[i].length;
          }
        }
        if (fool < 4 && symb !== "") {
          symbolSellection.push(symb);
        }
      }
    }
    for (let i = 0; i < symbolSellection.length; i++) {
      checkLinesElements(symbolSellection[i]);
    }
  };

  const setBetZero = () => {
    bet = 0;
  };

  const setMyScore = (e) => {
    let scr = 0;

    if (bet > 0 && e > 0) {
      scr = scr + (e + bet * e);
      scr = Math.round(scr * 100) / 100;
    } else if (bet > 0 && e === 0) {
      scr = scr - (e + bet * e);
     
      scr = Math.round(scr * 100) / 100;
    } else if (bet === 0 && e > 0) {
    
      scr = Math.round((scr + e) * 100) / 100;
    }
   
    handleScore(scr);
    //  setScore(scr);
  };

  const checkFourFools = () => {
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];
   
        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
   
      if (fool === 4) {
        winningLines.push(i);
        drawScore(i, false);
      }
    }
  };

  const evaluateResults = () => {
    checkFourFools();
    checkElements();
    //highLightWinningLines(0);
    if (winningLines.length > 0) {
      for (let i = 0; i < winningLines.length; i++) {
        drawScore(winningLines[i], false);
      }
    } else {

      if (score - bet >= 0.2) {
        score = Math.round((score - bet) * 100) / 100;
        //  setScore(scr);
        //handleScore(scr);

        if (score < bet) {
          setBetZero();
        }
      } else if (score - bet === 0) {
        setBetZero();
        score = 0;
        //  setScore(scr);
        //handleScore(scr);
      }
    }
    //winningLines=[];
  };
  const checkFiveFools = () => {
    let fl = false;

    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];

        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
 
      if (fool === 5) {
        fl = true;
        winningLines.push(i);
        drawScore(i, true);
        //  highLightWinningLines(0);
        break;
      }
    }

    return fl;
  };

  const checkWin = () => {

    if (gameOn > 0) {
      let fool = checkFiveFools();
      if (!fool) {
        evaluateResults();
      }
    }

  };

  return (
    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
      <div className="container">
        <div className="playContainer">
          <div className="reelSet">
            {renderReels(1)}
            </div>
        </div>
      </div>
      <div id="controls">
        <div
          className={!rolling ? "roll rolling" : "roll"}
          onClick={() => {
            spinReel();
          }}
          disabled={rolling}
        >
          {rolling ? "Rolling..." : "ROLL"}
        </div>
        <Withdrawal />
        <Bet score={score} setBet={callbackBet} />
        <label>
          Score:<span id="score">{score}</span>
        </label>
      </div>
    </>
  );
};

export default ReelSet;
