import React, { useRef,useEffect, useState } from "react";
import "../assets/css/App.css";
import images from "../assets/images/ImagesExplained";
import {motion} from "framer-motion";


const GameExplained = () => {

    const [width, setWidth] = useState(0);
    const carousel = useRef();

    useEffect(()=>{
        console.log(carousel.current.scrollWidth, carousel.current.offsetWidth);
        setWidth(carousel.current.scrollWidth - carousel.current.offsetWidth);
    },[]);

    const getImages = () =>{
        console.log("length imageArr is :"+images.length);
        let imgeArr = images.map((image)=>{
            return(
                <motion.div className="item">
                    <img className="myImage" src={image} alt=""/>
                </motion.div>
            )
        })

        console.log("Image lenght is: "+imgeArr.length);

        return imgeArr;
    }

    return(
        <motion.div ref={carousel} className="carousel" whileTap={{cursor:"grabbing"}}>
            <motion.div 
                className="inner-carousel"
                darg="x"
                dragConstraints={{right:0}}
            >
                {getImages()}
            </motion.div>

        </motion.div>
    )
}

export default GameExplained;
