import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import {motion,useAnimation, variants} from 'framer-motion';
import '../assets/css/App.css';

let opacity =1;

const Reel = (props) => {
  //42 symbols
//  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPKKQQAAG";
//const symbols = "DDDGNNSS";
  let reelRef= useRef();
  const symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";
  let reels = [];


  let currentScrollPos = 0;



  if(props.gameOn===0){
      reelSymbols=symbols[props.index].split("");
  }else{
    reelSymbols= symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
  }  
// console.log("My number is: "+(-(props.gameOn/242)));
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))]);
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+1]);
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+2]);

// let getReels="";


const callbackReels = (img, idx) => {
    reels[idx]=img;
}

console.log("How many images we've added ? "+reels.length);


useEffect (()=>{
 // console.log("My symbolsRefs from Reel is: "+reelRef.current.childNodes[0].attributes[2].nodeValue);

    reelRef.current.childNodes[0].attributes[2].nodeValue=" opacity: 0.3; transform: none;"
    reelRef.current.childNodes[1].attributes[2].nodeValue=" opacity: 0.3; transform: none;"
    reelRef.current.childNodes[2].attributes[2].nodeValue=" opacity: 0.3; transform: none;"

})

const variants =  {
    spin:{ 
      y: props.spin,
      opacity:1,
      transition: {
          duration:1,
        }
      
    },
    transition:{ transition: {
      delay: 0.1*Math.floor(props.index/3)
    }},
    hidden: {opacity: 0},
    duration: {duration:10}
}

const scrollByOffSet = (offSet) => {
    currentScrollPos = currentScrollPos + (-1 * Constants.REEL_WIDTH * offSet);    
    console.log("My currentScrollPos is:"+currentScrollPos);
}

const createArray = () =>{
    
        reelSymbols.map((el, idx) => {
        return  <Symbol symbol={el}  key={idx} index={idx} spin={props.gameOn} setImageReels={callbackReels}/>;
        })

     return <>{reelSymbols}</>   
}

const setupReel= ()=>{
  return (
  <div className="reel">
    <div className="customReel">
        <motion.div animate="spin" variants={variants} className="icons" ref={reelRef} >
           {createArray}
      </motion.div>
    </div>
  </div>
);
}

  return (
      <>{setupReel()}</>
    );
}
export default Reel;


