import React, { useState,useEffect } from "react";
import Bet from './Bet';
import CalculateScore from './CalculateScore';
import Withdrawal from './Withdrawal'; 

let bet=0;

const ScoreBet = (props) => {

  const [score, setScore] = useState(2);
  const [withdrawal, setWithdrawal] = useState(false);
  
  const callbackBet = (e) => {   
      bet=e;
      props.callBackSetBet(e);
  }

  const callbackScore = (scr) => {
    console.log("Withdrawal callbackScore is "+withdrawal);
    if(!withdrawal){
      setTimeout(()=>{
        setScore(scr);
      },350)
  }else{
    console.log("Withdrawal callbackScore is "+withdrawal);
    setScore(callBackGetScore());
    setWithdrawal(false);
  }
  }

  const callBackGetScore = () => {
    return score;
  }

  const callbackSetMyScr = (myScr) => {
    if(myScr===0){
 //     setTimeout(()=>{
        setScore(myScr);
        setWithdrawal(true);
        console.log("Withdrawal callbackSetMyScr is "+withdrawal);
 //     },350)
    //needs to be fixed
//    refScore.current.childNodes[2].disabled=true;
//    score=0;
      props.callBackSetBet(0);
 //   btnDisable=true;
  }else if(myScr>0){
 //   setTimeout(()=>{
      setWithdrawal(true);
      console.log("Withdrawal callbackSetMyScr is "+withdrawal);
      setScore(myScr);
 //   },350)
  }
    //score=myScr;
  }

  return (
    <>
      <Withdrawal myScore={callBackGetScore} callbackSetMyScore={callbackSetMyScr}/>
      <Bet block ={props.block} score={score} setBet={callbackBet} />
      <CalculateScore spinResults={props.resultsArray} spinResultsIdx={props.resultsArrayIdx} myBet={bet} setCallbackScore={callbackScore} game={props.game} winningLinesCallBack={props.winningLinesCallBack} looserCallBack={props.looserCallBack}/>
    </>
  );
};
export default ScoreBet;
