import React,{useState} from 'react';
import Symbol from './Symbol';
import Constants from '../Constants';

const Reel = async (props) => {

  const symbols=Constants.REEL_SYMBOLS[props.index];
  const reelSymbols = this.symbols.repeat(Constants.REELS_REPEAT).split("");
  const symbolHeight = this.props.height/ Constants.SYMBOLS;

  const symbolsRefs = [];


  const buildImagesReel = async() =>{

    return(
        props.reel[props.index].map((image, imageIndex)=> {
          return <Symbol key={imageIndex} index={imageIndex} img={image}/>
        })
      )
  }

  return(
    <>{buildImagesReel()}</>
  )

}
