import React, {useState} from 'react';
import Constants from '../Constants';
import Symbol from './Symbol';



const Reel = (props) =>{
    //42 symbols
    const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
    const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");
//    const [reelEffect,setReelEffect] = useState(0);

    const randomNr = () => {
        return Math.floor(Math.random() * 42) + 1;
   };

    let reelEffect= -(randomNr()*242);

    console.log("My math random is: "+ (Math.floor((Math.random() * 42)+1)));
    console.log("My props.st: "+props.st);

    const renderImages = () =>{
        let imagesList = reelSymbols.map((el, idx)=>
            {return <Symbol symbol={el} key={idx} index={idx} spin={props.st} />
         })
         return <>{imagesList}</>
        }

    return(
        
        <div className="reel" >
            {renderImages()}
        </div>
    )
}
export default Reel;
