import React, { useEffect, useState } from "react";
import {ethers} from "ethers";
import ReelSet from "./components/ReelSet";
import "./assets/css/App.css";
import Mint from "./components/Mint";
import PFPRacerNFT from './artifacts/contracts/PFPRacerNFT.sol/abi.json'
import { assert } from "chai";
import axios from 'axios';

const App = () => {
  const [spin, setSpin] = useState(0);
  const [connecting, setConnecting] = useState();
  const [showList, setShowList] = useState(0);
  const [welcomeMessage, setWelcomeMessage] = useState("");
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState("");
  const contractAddr = "0x01e06a0795d91D346B0C441439001A044AdE67c8";
  const callbackSetSpin = (spin) => {
    console.log("My App score is: " + spin);
    setSpin(spin);
  };

  const callBackShowWin = () => {
    setShowList(1);
  };

  const welcomeWallMessage = () => {
    if(welcomeMessage===""){
      return (
        <div className="welcome">
        <div className="div-connect">   
        <h2 className="h2-title">Racer NFT Slot Machine</h2>
          <button
              className={connect ? "connect connecting" : "connect"}
              onClick={() => {checkConnection()}}
          >Connect to Metamask Wallet</button>
        </div> 
      </div>);
  }else{
    return(
    <div className="welcome">
      <Mint account={account} contract={contract} /> 
      <h2 id="h2-connect">Welcome Player :
        <p id="p-welcomeMessage">{welcomeMessage}</p>
        </h2>
    </div>
    );
  }
  }
/*
I need to retrieve withdraw
*/


  const checkConnection = async() =>{
    if(typeof window.ethereum !== "undefined"){
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });

        if(accounts[0]!==undefined){
          setWelcomeMessage(accounts);
          const provider =  new ethers.providers.Web3Provider(window.ethereum);
            const newSigner =  provider.getSigner();

            // console.log("My chain id is: "+ newSigner.getChainId().then(val => val)
            // .catch(err => err.message));
          let chainId = await newSigner.getChainId();
          console.log("My chain id is:"+chainId);
          if(chainId!==80001){
            alert("You are not connected the Correct network please switch to: chainID:80001 RPC URL:https://matic-mumbai.chainstacklabs.com");
          }else{
            setAccount(accounts[0]);
            setContract(
              new ethers.Contract(
                contractAddr,
                PFPRacerNFT.abi,
                newSigner
              )
            )
          }
        }else{
          setWelcomeMessage(account);
        }
  }else{
    alert("Metamask not installed !!! Please install metamask in order to place a mint or do a withdrawal !!!");
  }
}

const connect = async () => {
    if(typeof window.ethereum !== "undefined"){
      const accounts = await window.ethereum.request({
        method: "eth_requestAccounts",
      });

      if(accounts[0]===undefined){
        const provider = await new ethers.providers.Web3Provider(window.ethereum);
        const newSigner = await provider.getSigner();
        setAccount(accounts[0]);
        setContract(
          new ethers.Contract(
            contractAddr,
            PFPRacerNFT.abi,
            newSigner
          )
        )

        setWelcomeMessage("Welcome player: "+account+" !");
     }else{
      assert("You already are connected to "+account);
     }
    }else{
      alert("Metamask not installed !!! Please install metamask in order to place a mint or do a withdrawal !!!");
    }
  }

  useEffect(()=>{
    checkConnection();
    welcomeWallMessage();
    console.log("My welcome message is "+welcomeMessage);
  },[account])

  return (
    <>
      <div>{welcomeWallMessage()}</div>    
      <ReelSet
        runGame={spin}
        spinCallback={callbackSetSpin}
        showWinCallback={callBackShowWin}
      />
    </>

  );
};
export default App;
