import React from "react";
import ReactDOM from "react-dom";
import "../assets/css/App.css";
import ReactSimpleCarousel from "react-spring-carousel";
import  images from "../assets/images/ImagesExplained";
import { useSpringCarousel } from 'react-spring-carousel';


const GameScoreCarousels = () =>{
    const { 
        carouselFragment, 
        slideToPrevItem, 
        slideToNextItem 
      } = useSpringCarousel({
        itemsPerSlide: 3, 
        withLoop: true,
        items: images.map((i) => ({
          renderItem: (
            <div className="item" key={i}>
                    <img className="myImage" src={i} alt=""/>
                </div>
          ),
        })),
      });
    return (
    <>
        <div className="winningPatterns">
            <h2 className="h2-title">Winning Patterns and Values</h2>
        </div>
        <div className="carousel">
            <div className="inner-carousel" >
            <div>
        <button onClick={slideToPrevItem}>Prev item</button>
        {carouselFragment}
        <button onClick={slideToNextItem}>Next item</button>
      </div>
            </div>
      </div>
    </>
        )
}

export default GameScoreCarousels;
