import React,{ Component} from 'react';
import { Animate } from "react-move";
import Constants from '../Constants';
import Symbol from './Symbol';

class Reel extends Component{
    constructor(props){
        super(props);

        this.state={
            width:0,
            height:0
        }

    }


    render(){
        const reel= {
            width:this.state.height / Constants.REELS,
            height: 800,
            flex:1,
            flexDirection: "row",
            backgroundColor:"pink"
        }
        return( 
            <div style={reel}>

            </div>
        )
    }
}

export default Reel;
