import React, { useEffect, useState } from "react";
import {ethers} from "ethers";
import ReelSet from "./components/ReelSet";
import "./assets/css/App.css";
import Mint from "./components/Mint";
import PFPRacerNFT from './artifacts/contracts/PFPRacerNFT.sol/abi.json'

const App = () => {
  const [spin, setSpin] = useState(0);
  const [connecting, setConnecting] = useState();
  const [showList, setShowList] = useState(0);
  const [welcomeMessage, setWelcomeMessage] = useState("");
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState("");

  const callbackSetSpin = (spin) => {
    console.log("My App score is: " + spin);
    setSpin(spin);
  };

  const callBackShowWin = () => {
    setShowList(1);
  };

  const welcomeWallMessage = () => {
    if(welcomeMessage===""){
      return (
        <div id="jackpot">
          <h2>Racer NFT Slot Machine</h2>
          <p>Welcome player !</p>
        </div>);
  }else{
    return(
    <div id="jackpot">
         <h2>Racer NFT Slot Machine</h2>
         <p>{welcomeMessage}</p>
    </div>
    );
  }
  }

  const checkConnection = () =>{
    console.log("My account is "+account);
    if(account!=""){
      console.log("I am inside if");
      setWelcomeMessage("Welcome player: "+account+" !");
    }
  }

  const connect = async () => {
    if(typeof window.ethereum !== "undefined"){
      const accounts = await window.ethereum.request({
        method: "eth_requestAccounts",
      });
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const newSigner = provider.getSigner();
      setAccount(accounts[0]);
      setContract(
        new ethers.Contract(
          "0xEE8E55e5BE8fAB0aa31D23eeb0F976baB4C2E557",
          PFPRacerNFT.abi,
          newSigner
        )
      )
      setWelcomeMessage("Welcome player: "+account+" !");
    }else{
      console.log("Please install metamask");
    }
  }

  useEffect(()=>{
    checkConnection();
    console.log("My welcome message is "+welcomeMessage);
  },[account])

  return (
    <>
      <>{welcomeWallMessage()}</>
      <div
          className={connect ? "connect connecting" : "connect"}
          onClick={() => {
            connect();
          }}
        >
          Connect Wallet
        </div>

      <ReelSet
        runGame={spin}
        spinCallback={callbackSetSpin}
        showWinCallback={callBackShowWin}
      />
    </>
  );
};
export default App;
