import React, { useState, useRef, useEffect } from "react";

const Bet = (props) => {
  const [bet, setBet] = useState(0);
  
  const increaseBet = () => {

    if (bet < props.score - 0.2 && props.score !== 0) {
      console.log("Bet is set !");
      setBet(Math.round((bet + 0.2) * 100) / 100);
    } else {
        setBet(props.score);
    }
  };

  const decreaseBet = () => {
    if (bet >= 0.2 && props.score !== 0) {
        setBet(Math.round((bet - 0.2) * 100) / 100);
    } else if (bet === props.score && props.score !== 0) {
        setBet( Math.round((props.score - 0.2) * 100) / 100);
    }
  };

//  useEffect(()=>{
//    props.getScore(bet);
//  },[bet])

  return (
    <>
      <button
        id="setBet"
        onClick={()=> increaseBet()}
        //  disabled={state.disabled}
      >{props.setBet(bet)}
        +
      </button>
      <div className={!bet ? "bet rolling" : "bet"}>Bet:{bet}</div>
      <button
        id="setBet"
        onClick={()=>decreaseBet()}
        //  disabled={this.state.disabled}
      >{props.setBet(bet)}
        -
      </button >
    </>
  );
};
export default Bet;
