import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import { motion } from "framer-motion";

const Reel = (props) => {
  //42 symbols
  //  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPKKQQAAG";
  //const symbols = "DDDGNNSS";
  let reelRef = useRef();
  const symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";
  let reelSymbolsList = [];


  useEffect(() => {
    console.log("My opacity useEffect is: " + props.opacity);
  }, []);

  if (props.gameOn === 0) {
    reelSymbols = symbols[props.index].split("");
  } else {
    reelSymbols = symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
  }
  // console.log("My number is: "+(-(props.gameOn/242)));
  // console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))]);
  // console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+1]);
  // console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+2]);

  // let getReels="";

  const setupReel = () => {
    if (props.gameOn === 0) {
      return (
        <motion.div className="reel">
          {reelSymbols.map((el, idx) => {
            return (
              <Symbol
                symbol={el}
                key={idx}
                index={idx}
                spin={props.gameOn}
                opacity={1}
              />
            );
          })}
        </motion.div>
      );
    }else if(props.gameOn<0){
        return (
            <motion.div className="reel">
              {reelSymbols.map((el, idx) => {
                return (
                  <Symbol
                    symbol={el}
                    key={idx}
                    index={idx}
                    spin={props.gameOn}
                    opacity={1}
                  />
                );
              })}
            </motion.div>
          );
    }
    else if(props.gameOn<0 && props.opacity<1){
        <motion.div className="reel"   key={props.index}          
        animate={{ y: props.spin, opacity: props.opacity }}
      
        variants={variants}
        initial="hidden">
        {reelSymbols.map((el, idx) => {
          return (
            <Symbol
              symbol={el}
              key={idx}
              index={idx}
              spin={0}
              opacity={1}
            />
          );
        })}
      </motion.div>
    }
  };

  return <>{setupReel()}</>;
};
export default Reel;

