import  React, { useEffect, useState, useRef } from "react";
import '../assets/css/App.css';
import {ethers} from "ethers";
import PFPRacerNFT from '../artifacts/contracts/PFPRacerNFT.sol/abi.json'
import axios from 'axios';

const Withdrawal=(props)=>{
    const [rolling, setRolling] = useState(false);
    const [name, setName] = useState("");
    const [account, setAccount] = useState("");
    const [contract, setContract] = useState("");
    let refCode ="";
    let smartKey = "";
    

    const createSmartKey = async (refCode)=>{
  //    let hashCode="dbabe1135898ac301cfb00e6ce2122515ccae2390fc4c304eef3523a8f69843b";
      let hashCode="";
      const customConfig = {
        headers: {
        'Content-Type': 'application/json'
        }
      };

      const dataToSend = JSON.stringify({ "refCode": refCode });
      console.log("My JSON dataToSend is: "+dataToSend);
     // console.log("Creating the smartKey:"+ requestOptions);

 //     axios.post("https://polar-bastion-25190.herokuapp.com/api/post_refCode", {
  axios.post("https://localhost:3333/api/post_refCode", {
      dataToSend ,customConfig
      }).then(response => {
        console.log(response.data);
        hashCode=response.data;
      }, error => {
        console.log(error);
      });

      // await axios.get("https://polar-bastion-25190.herokuapp.com/api/keccak256Hash")
      //   .then(resp => {
      //       hashCode=resp.data;
      //       console.log("My resonse data in get is: "+hashCode);
      //   });


        console.log("My hash code is: "+hashCode);  
      return hashCode;
    }

    // const createSmartKey = async (refCode) =>{
    //  await fetch('http://localhost:8000/secrets').then(res => {
    //     if(res.ok){
    //       return res.json()
    //     }
    //   }).then(jsonResponse => console.log(jsonResponse.secretKey) )

    //   console.log("smartkey = "+smartKey);
    //   console.log("smartKey + refCode = "+smartKey+refCode);
    // }

    const initConnection = async () =>{
      if(typeof window.ethereum !== "undefined"){
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const newSigner = provider.getSigner();
        setAccount(accounts[0]);
        setContract(
          new ethers.Contract(
            "0xEE8E55e5BE8fAB0aa31D23eeb0F976baB4C2E557",
            PFPRacerNFT.abi,
            newSigner
          )
        )
      }else{
        console.log("Please install metamask");
      }
    }


    useEffect(()=>{
      initConnection();
    },[account]);

  const makeWithdrawal = async(refCode) => {
    console.log("My refCode in makeWithdrawal is: "+refCode);
    let withdrawalAmount = Math.floor(props.getScoreCallback());
    console.log("My score in makeWithdrawal is "+withdrawalAmount);
     if(withdrawalAmount > 0){
    window.confirm("Please be aware: if you have 1.2 or 20.2 NFT's you will be able to withdrawal only the integer part like 1 or 2.");
    //await contract.withdrawal(refCode);
    console.log("This is the withdrawal function !!!");
    
    await contract.withdrawal(withdrawalAmount,refCode);
    
    let myScr= props.getScoreCallback()-withdrawalAmount;
    console.log("My score is "+myScr);
    //props.callbackSetScore(myScr);
    props.setScoreCallBack(myScr);
    }
  }

  const checkWallet = async()=>{
    // /checkUserWallet, setCheckUserWallet
    let results=false;
    const customConfig = {
      headers: {
      'Content-Type': 'application/json'
      }
    };
    const dataToSend = JSON.stringify({ "wallet": account });
    console.log("My checkwallet data to send is:"+dataToSend);
    
    await axios.post("http://localhost:3333/api/getWalletVerify", {
      dataToSend, customConfig
    }).then(response => {
      console.log("My checkWallet response data is"+response.data);
      if(response.data>0){
        results=true;
      }
    }, error => {
      console.log(error);
    });

    return results;
  }

  const insertWalletIntoDB = async()=>{
    const withdrawalAmount=Math.floor(props.getScoreCallback());
    const customConfig = {
      headers: {
      'Content-Type': 'application/json'
      }
    };

    const dataToSend = JSON.stringify({ "wallet": account,
                                        "withdrawal_amount":withdrawalAmount});
   
   console.log("My insertwalletintodb dataToSend is: "+dataToSend);
   // console.log("Creating the smartKey:"+ requestOptions);

//     axios.post("https://polar-bastion-25190.herokuapp.com/api/post_refCode", {
   await axios.post("http://localhost:3333/api/insertWallet", {
    dataToSend ,customConfig
    }).then(response => {
      console.log("My insertwalletintodb response is"+response.data);
    }, error => {
      console.log(error);
    });
  }
    
  const withdrawal = async() => {
    console.log("My withdrawal is: "+props.blockWithdraw);
    let respCheckWallet = await checkWallet();
    console.log("My respCheckWallet is: "+respCheckWallet);
    if(!respCheckWallet){
      await insertWalletIntoDB();
    if(!props.blockWithdraw){
      let score = Math.floor(props.getScoreCallback());
        console.log("Withdrawal has been activated");
        console.log("My account is: "+account);
      if(score>0){
        refCode = prompt("Please enter your refCode:", "RefCode");
        if (refCode === null || refCode === "" || refCode !== "RACECOUPLE2022") {
          let text = "Wrong RefCode ! But you can Mint !";
          window.confirm(text)
        } else {
      //checkAccount and insert to database

        let hashKey = await createSmartKey(refCode);
        console.log("My hashKey in withdrawal is:"+hashKey);
        makeWithdrawal(hashKey);
      }
    }else{
      window.confirm("You already had your change ! But you can still Mint !");
    }
  }else{
    window.confirm("You need to have at least 1 NFT to withdrawal");
  }
} 
};

    return(
    <div
         className={withdrawal ? "roll rolling" : "roll"}
         onClick={() => {withdrawal()}}
    >Withdrawal</div>


    )
}

export default Withdrawal;
