import React,{useState} from 'react'
import Withdrawal from './Withdrawal';
import ScoreBet from './ScoreBet'

const Controller = (props) =>{
    const [rolling, setRolling] = useState(false);
    let bet=2;
    let score=2;

    const setBetCallBack=(myBet)=>{
        console.log("Setting Controller bet to: "+myBet);
        bet=myBet;
    }

    const setScoreCallBack = (myScore) =>{
        score=myScore;
    }
    
    const callbackSpin =()=>{
        console.log("Calling Spin !!!"+bet);
        if(bet===0){
            console.log("Please place a bet");
        }else{
                props.spinReel();
        }
    }

    return(
        <div id="controls">
        <div
          className={!rolling ? "roll rolling" : "roll"}
          onClick={() => {
            callbackSpin();
          }}
          disabled={rolling} >
          {rolling ? "Rolling..." : "ROLL"}
        </div>
        <Withdrawal/>
        <ScoreBet resultsArray={props.myResults} resultsArrayIdx={props.myResultsIdx} callBackSetBet={setBetCallBack} callbackSetScore={setScoreCallBack} game={props.game} />
          
      </div>
    )
}

export default Controller;
