let score=0;

const ScoreBet = (props) => {

  const callbackBet = (e) => {    
    console.log("CallBackBet ="+e);
    bet=e;
  }

  const callbackScore = (scr)=>{
    setTimeout(()=>{
      console.log("My current score is:"+score);
    //  score=scr;
      console.log("Score callback setting score to: "+score);
  },7500)
  }

console.log("My bet inside ScoreBet is: "+bet);

  return (
    <>
      <Bet score={score} setBet={callbackBet} />
      <CalculateScore spinResults={props.resultsArray} spinResultsIdx={props.resultsArrayIdx} setCallbackScore={callbackScore}/>
    </>
  );
};
export default ScoreBet;
