import  React, { useEffect, useState, useRef } from "react";
import '../assets/css/App.css';
import {ethers} from "ethers";
import PFPRacerNFT from '../artifacts/contracts/PFPRacerNFT.sol/abi.json';

const Mint = (props) =>{
    const [mintAmount, setMintAmount] = useState(1);
    const [leftToMint, setLeftToMint] = useState(0);

    const howManyNFTsAreLeft = async () =>{
        let totalNFT = await props.contract.maxSupply();
        let mintedNFT= await props.contract.getNFTBalance(); //to be changed into getNFTBalance
        console.log("My totalNFT's in howManyNFTsAreLeft is : "+totalNFT);
        console.log("My total mintedNFT is"+mintedNFT);
        let nftLeftToMint = totalNFT - mintedNFT;
        setLeftToMint(nftLeftToMint);
        console.log("Total NFT's left to mint "+nftLeftToMint);
      }

const handleSubmit= async() =>{
    let cost = await props.contract.cost();
    cost = cost*mintAmount;
    console.log("Price is: "+cost);

    await props.contract.mint(mintAmount, {value: cost});

//    let nftTx = await contract.mint(mintAmount);
//    console.log("We are in handleSubmit: "+mintAmount);
    //mintAmount.preventDefault();
//,{ gasPrice: 33e9 }
  //  let gasPrice = await props.contract.getGasPrice();
    
   // console.log("Gas price is: "+gasPrice);
//   /  let nftTx = await contract.mint(mintAmount);
 //   console.log('Mining....', nftTx.hash)
 //   let tx= nftTx.wait();
  //  let nftTx = await props.contract.mint(mintAmount);
  //  let tx = nftTx.wait();
 //   console.log("Mined!", tx);
   // console.log("Mined!", tx);
   //await contract.withdrawal(mintAmount, "dbabe1135898ac301cfb00e6ce2122515ccae2390fc4c304eef3523a8f69843b");
    // howManyNFTsAreLeft();
  //  alert(`We have minted ${mintAmount}`)
    
}

    useEffect(()=>{
        howManyNFTsAreLeft();
    },[mintAmount])

      return (
        <>
        <form className="form">
        <label className="label-mint">
             NFT's left to mint:
             <p className="p-welcomeMint">{leftToMint}</p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        </label>
          <label className="form-label">
            Mint Amount:
            <input
              type="text"
              value={mintAmount}
              onChange={e => setMintAmount(e.target.value)}
            />
          </label>
          <button className="form-submit-button" onClick={()=>{handleSubmit(mintAmount)}}>Mint</button>
        </form>
       
        
        </>
    
      );
}



export default Mint;
