import React, { useRef,useEffect, useState } from "react";
import "../assets/css/App.css";
import images from "../assets/images/ImagesExplained";
import {motion} from "framer-motion";


const GameExplained = () => {

    const [width, setWidth] = useState(0);
    const carousel = useRef();

    useEffect(()=>{
        console.log("From carousel ! = > "+carousel.current.scrollWidth, carousel.current.offsetWidth);
        setWidth(carousel.current.scrollWidth - carousel.current.offsetWidth);
    },[]);

    const mapImages = async () =>{
       return images.map((image)=>{
            return(
                <motion.div className="item" key={image}>
                    <img className="myImage" src={image} alt=""/>
                </motion.div>
            )
        })
    }


    return(
        <>
        <div className="winningPatterns">
            <h2 className="h2-title">Winning Patterns and Values</h2>
        </div>
        <motion.div ref={carousel} className="carousel" >
            <motion.div 
                drag="x"
                dragConstraints={{right:0, left:-width}}
                className="inner-carousel"
            >
                {mapImages}
            </motion.div>

        </motion.div>
        </>
    )
}

export default GameExplained;
