import React, { useState, useRef, useEffect } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";

const Reel = (props) => {
  //42 symbols
  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
  const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");
  let symbol = useRef();

  useEffect(()=>{
    console.log("my image is: "+symbol.current);
  },[])
  

  return (
    <div className="reel" >{reelSymbols.map((el, idx) => {
        return <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn} ref={symbol}/>;
    })} 
    </div>);
};
export default Reel;
