import React, { useEffect, useRef, forwardRef, useState } from "react";
import Image from "../assets/images/Images";
import { motion, animate, transform } from "framer-motion";

let active = true;

const Symbol = (props) => {
  let image = "";

  const getImage = () => {
    if (props.spin < 0) {
      switch (props.symbol) {
        case "A":
          return Image.America;
        case "G":
          return Image.Aragon;
        case "N":
          return Image.Argentina;
        case "S":
          return Image.Assen;
        case "U":
          return Image.Australia;
        case "C":
          return Image.Catalunya;
        case "F":
          return Image.Finland;
        case "R":
          return Image.France;
        case "Y":
          return Image.Germany;
        case "I":
          return Image.Indonesia;
        case "J":
          return Image.Japan;
        case "Z":
          return Image.Jerez;
        case "M":
          return Image.Malaysia;
        case "O":
          return Image.Misano;
        case "L":
          return Image.Mugello;
        case "T":
          return Image.Osterreich;
        case "P":
          return Image.Portugal;
        case "Q":
          return Image.Qatar;
        case "H":
          return Image.Thailand;
        case "K":
          return Image.UK;
        case "D":
          return Image.Fool;
        case "V":
          return Image.Valencia;
        default:
          return Image.Fool;
      }
    } else {
      return Image.Fool;
    }
  };

  image = getImage();

  const spinIt = (e) => {
    console.log("e= "+e);
    if(e==="spin"){
    return (
      <motion.img
        animate={{ y: props.spin, opacity: active ? 1 : 0.3 }}
        transition={{
          ease: "easeOut",
          delay: 0.3,
          x: { stiffness: 50, damping: 3 },
          default: { duration: 0.5 },
        }}
        src={image}
        alt=""
        value={image}
      />
    );
  }if (e==="showWin"){
    return(

      <motion.img
      animate={{ opacity: active ? 1 : 0.3 }}
      transition={{
        ease: "easeOut",
        delay: 0.3,
        x: { stiffness: 50, damping: 3 },
        default: { duration: 0.5 },
      }}
      src={image}
      alt=""
      value={image}
    />

    );
  }
  };

  return (<div className="icons">
          {spinIt(props.arg)}
        </div>);
};

export default Symbol;

