import React, { useState, useRef, useEffect } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";

const Reel = (props) => {
  //42 symbols
  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
  let symbolsRefs = "";
  let reelSymbols = "";

  if(props.gameOn===0){
      reelSymbols=symbols.split("");
  }else{
    reelSymbols= symbols.repeat(Constants.REELS_REPEAT).split("");
  }  

  let symbol = useRef();

console.log("props.gameOn ="+props.gameOn);
  

  return (
    <div className="reel" ref={symbol}>{reelSymbols.map((el, idx) => {
        return <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn} ref={(ref) => { symbolsRefs[idx]=ref; }}/>;
    })} 
    </div>);
};
export default Reel;
