import React,{ Component} from 'react';
import { Animate } from "react-move";
import Constants from '../Constants';
import Symbol from './Symbol';

class Reel extends Component{
    constructor(props){
        super(props);

        this.state={
            width:Constants.MAX_HEIGHT,
            height:Constants.MAX_HEIGHT
        }

    }


    render(){
        const reel= {
            width:this.state.width/Constants.REELS,
            height: Constants.MAX_HEIGHT,
            flexDirection: "row",
            backgroundColor:"red"
        }
        return( 
            <div style={reel}>
                <p>This is a test !</p>
            </div>
        )
    }
}

export default Reel;
