import React, { useState,useRef, useEffect} from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import { motion } from "framer-motion";

let offset=0;
let reelIdx=[];

const Reel = (props) => {

  let symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";
  let reels = [];
  let randomNr=3;
  let opacity=props.opacity;

  let symbolRef = useRef();
  let reelRef = useRef([]);

  const randomBetween = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
  };

  const generatedSymbols =(randomNr)=>{
    //   for(let i=0; i<3;i++){
    //       element=(symbols[props.index])[(randomNr/246)+i];
    //       reelIdx.push(element);
    //   }
       let elementPos=-(randomNr/246);

       for(let i=0; i<3;i++){
        reelIdx.push(reelSymbols[elementPos+i]);
       }
     }

  const getRandomNr = () => {
    if (props.game > 0) {
    randomNr = -(randomBetween(
        (Constants.REELS_REPEAT - 2) * symbols[props.index].length,
        (Constants.REELS_REPEAT - 8) * symbols[props.index].length
      ) * 246);
     
    }

    if(offset===randomNr){
        getRandomNr();
    }
   else{
        offset=randomNr;
    }
    console.log("randomNr = "+randomNr);
    generatedSymbols(randomNr);
  };

  if (props.game === 0) {
    reelSymbols = symbols[props.index].split("");
  } else if(props.game>0){
    reelSymbols = symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
    getRandomNr();
  }


  const sendArrayList = ()=>{
    if(reelIdx.length===3){
        props.createArray(reelIdx);
        reelIdx=[];
    }
   
  }

  useEffect(() => {
 
  },[]);

  const createReels = () =>{
    reels=reelSymbols.map((el, idx) => {
        return (
          <Symbol symbol={el} key={idx} index={idx} spin={props.game} />
        );
      })
      return <>{reels}</>
  }

  const variants = {
    initial:{
        opacity: 1 
    },
    spin: {
       y:offset,
       opacity:opacity, 
       transition:{ 
            duration:3, 
            delay:props.index * 0.8 
        },
    },
    exit : { opacity: 1 }
  };
 
  return (
    <motion.div className="reel" >
      <motion.div key={props.index} variants={variants}  initial="initial"  animate="spin" className="icons" ref={symbolRef}>
        {createReels()}{sendArrayList()}
      </motion.div>
    </motion.div>
  );
};
export default Reel;

