import React, {useEffect, useState} from 'react';
import "../assets/css/App.css";
import  images from "../assets/images/ImagesExplained";

const GameScoreCarousels = () =>{

    const [currentIndex, setCurrentIndex] = useState(0);
    const carouselInfiniteScroll = () =>{
        if(currentIndex===images.length-1){
            return setCurrentIndex(0);
        }
        return setCurrentIndex(currentIndex+1);
    }

    useEffect(()=> {
        const interval = setInterval(()=> {carouselInfiniteScroll()}, 3000);
        return () => clearInterval(interval)
    })

    return (
    <>
        <div className="winningPatterns">
            <h2 className="h2-title">Winning Patterns and Values</h2>
        </div>
        <div className="carousel-container">
        {images.map((image)=>{
            return(
                <div className="item" key={image}>
                    <img className="myImage" src={image} alt=""/>
                </div>
            )
        })}
        </div>
    </>
        )
}

export default GameScoreCarousels;
