import React, {Fragment} from 'react';
import Constants from '../Constants';
import Symbol from './Symbol';
import motion from 'framer-motion';



const Reel = (props) =>{

    //42 symbols
    const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
    const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");

    function getRandomInt() {
        return Math.floor(Math.random() * 42);
      }

    if(props.reelSpin){
        const reelEffect=-(getRandomInt()*242);
    }

    return(
        
        <div className="reel" >
            {reelSymbols.map((el, idx)=>
                {return <Symbol symbol={el} key={idx} index={idx} spin={reelEffect} />
            })}
        </div>
    )
}
export default Reel;
