import React from 'react';
import Constants from '../Constants';
import Symbol from './Symbol';

const Reel = (props) =>{

    const symbols = "AAGGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQDDD";
    const symbolHeight = props.height/Constants.REELS ;
    const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");


    return(
        <div className="reel" >
            {reelSymbols.map((el, idx)=>
                {return <Symbol symbol={el} key={idx} index={idx} width={props.width} height={symbolHeight}/>
            })}
        </div>
    )
}
export default Reel;
