const CalculateScore = (props) => {

    const[score, setScore] = useState(2);
    
    let scoreRef = 0;
    //let score = 2;
    let bet =0;
    
    let results = [];

    let spinResults = props.spinResults;
  
  //  let bet = 0;
    let winningLines = [];
    let countWin = 0;
    let symbolSellection = [];
    let randomNr = 0;
  //  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
  //const symbols = "DDDGNNSS"
  const symbols = Constants.REEL_SYMBOLS;
  console.log("Our CalculateScore bet is: "+props.myBet)
  console.log("Our CalculateScore results: "+props.spinResults);
  console.log("Our CalculateScore resultsIDX are: "+props.spinResultsIdx);
  let scr=0;
  


  for(let i=0; i<1000;i++){
    scr=score+i;
  }


    
    props.setCallbackScore(scr);
    
   
    setTimeout(()=>{
        setScore(scr)
         },7500)
 

    return (      
        <label>
        Score:<span id="score">{score}</span>
    </label>
    )
}

export default CalculateScore;
