import React,{useState} from 'react';
import Reels from './Reel';
import '../assets/css/App.css';


const ReelSet = () => {

  const [gameOn, setGameOn] = useState(0);
  const [initialReelState, setInitialReelState] = useState([["D","D","D"],
                                                            ["D","D","D"],
                                                            ["D","D","D"],
                                                            ["D","D","D"],
                                                            ["D","D","D"],
                                                           ]);

const outputMyArray = () => {
  console.log("Outputing array !!!");
}

 const retrieveStateImages = ()=>{
  return  (
    <div id="reels">
    {initialReelState.map((reels, index) => {
        return(
            <div className="reel">
                <div className="icons"> 
                    <Reels key={index} reels={reels}/>
                </div>
            </div>
            );
        })
    }
    </div>
  )
 }

 return (
        <>{retrieveStateImages()}</>
 ) 

}

export default ReelSet;
