import  React, { useEffect, useState, useRef } from "react";
import '../assets/css/App.css';
import {ethers} from "ethers";
import PFPRacerNFT from '../artifacts/contracts/PFPRacerNFT.sol/abi.json';
import FormMint from "./FormMint";

const Mint = () =>{
    
    const [leftToMint, setLeftToMint] = useState(0);
    const [account, setAccount] = useState("");
    const [contract, setContract] = useState("");
    const url="https://vast-ridge-99820.herokuapp.com";
    const contractAddr = "0x26CD56EeeF4999E6E27030158ec038dd19B8b3cA";

    const howManyNFTsAreLeft = async () =>{
      let totalNFT="";
      let mintedNFT="";
      
      totalNFT = await contract.getMaxSupply();//to be changed into getNFTBalance
      console.log("totalNFT IS: "+totalNFT);
      mintedNFT= await contract.getContractNFTBalance(); 
      
        console.log("My totalNFT's in howManyNFTsAreLeft is : "+totalNFT);
        console.log("My total mintedNFT is"+mintedNFT);
        let nftLeftToMint = totalNFT - mintedNFT;
        setLeftToMint(nftLeftToMint);
        console.log("Total NFT's left to mint "+nftLeftToMint);
      }

      const insertWalletIntoDB = async()=>{

        const dataToSend = JSON.stringify({ "wallet": account,
                                            "txtype":"mint",
                                            "amount":mintAmount,
                                             });
       
       console.log("My insertwalletintodb dataToSend is: "+dataToSend);
          const link = url+"/api/insertWallet";
          await fetch(link,{
      //  await fetch('http://localhost:3333/api/insertWallet', {
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          body: dataToSend
        })
    
      }

const handleSubmit= async() =>{
  let transaction = "";
  let errorTx= false;
  let contractPaused = await contract.paused();
  if(!contractPaused){
    if(mintAmount>0&&mintAmount<1001){
        try{
            const{ethereum}=window;

            if(ethereum){
   
                let cost = await contract.cost();
                cost = cost*mintAmount;
                console.log("Price is: "+cost);
              try{
                transaction = await contract.mint(mintAmount, {value: cost});
              }catch(err){
                errorTx=true;
                alert("Transaction failed, please try again later ! Transaction has been reverted ! ");
              }
              if(!errorTx){
                alert("Minted !! You can see transaction here:" +"<a href="+`https://mumbai.polygonscan.com/tx/${transaction.hash}`+">Transaction</a>");
            
                // await axios.get(`https://mumbai.polygonscan.com/tx/${transaction.hash}`)
                // .then(resp => {
                //     response=resp.data;
                //     console.log("My resonse data in get is: "+response);
                // });
                insertWalletIntoDB();
                await howManyNFTsAreLeft();
                setMintAmount(0);
              }
             } else{
                console.log("Ethereum object does not exist !");
             }   
        }
        catch(err){
            console.log("This is myerror"+err);
        }
     

    }else{
      if(mintAmount===0){
        alert("You need to mint at least 1 NFT");
      }else if(mintAmount >1000){
        alert("Holly Molly what do you wanna do ? Hold your horses and leave some for the others too!!! ");
      }else{
        alert("Number only !!!")
      }
    }
}else{
  alert("Contract is PAUSED, you can't mint at this moment, please try again later");
}
}

const initConnection = async () =>{
  if(typeof window.ethereum !== "undefined"){
    const accounts = await window.ethereum.request({
      method: "eth_requestAccounts",
    });
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const newSigner = provider.getSigner();
    setAccount(accounts[0]);
    setContract(
      new ethers.Contract(
        contractAddr,
        PFPRacerNFT.abi,
        newSigner
      )
    )
  }else{
    console.log("Please install metamask");
  }
}

    useEffect(()=>{
        howManyNFTsAreLeft();
        initConnection();
    },[mintAmount])

      return (
        <>
        <div className="div-form">
          <FormMint/>
        </div>
        <button className="form-submit-button" onClick={()=>{handleSubmit(mintAmount)}}>Mint</button>
     </>
      );
}

export default Mint;
