import React, {useState} from 'react'
import '../assets/css/App.css'
import ReelSet from './ReelSet';

const Controls = (props) => {

    const [rolling, setRolling] = useState(false);
    const [bet, setBet] = useState(0);
    const [score, setScore] = useState (2);

    const increaseBet = () => {
        if(bet>=0&&score>=0.2){
          setBet( Math.round((bet+0.2)*100)/100);
        }
      }
    
      const decreaseBet = () => {
        if(bet>0){
          setBet( Math.round((bet-0.2)*100)/100);
        }
      }

      const withdrawal=()=>{
        console.log("Withdrawal class !");
      }

    return(
        <div id="controls">
  <div
    className={!rolling ? "roll rolling" : "roll"}
    onClick={() => {
    //  this.spin();
      // this.reelSet.spin();
      //    console.log("My reelset score is:"+this.reelSet.onScore());
  //  this.setMyScore(this.onScore());

      console.log("executing now");
    }}
    disabled={rolling}
  >
    {rolling ? "Rolling..." : "ROLL"}
  </div>
  <div
    className={!withdrawal ? "roll rolling" : "roll"}
    onClick={() => {}}
  //  disabled={withdrawal}
  >
    {withdrawal ? "Withdrawaling..." : "Withdrawal"}
  </div>
  <button
    id="setBet"
    onClick={increaseBet}
  //  disabled={state.disabled}
  >
    +
  </button>
  <div className={!bet ? "bet rolling" : "bet"}>
    Bet:{bet}
  </div>
  <button
    id="setBet"
    onClick={decreaseBet}
  //  disabled={this.state.disabled}
  >
    -
  </button>
  <label>
    Score:<span id="score">2</span>
  </label>
  </div>
    )
}

export default Controls;
