import React, { useState, useRef, useEffect } from "react";
import Constants from "../Constants";
import "../assets/css/App.css";
import Reel from "./Reel";

const ReelSet = (props) => {
  //  const [spinReel, setSpinReel] = useState(false)
  const spinResults = [];
  const winningLines = [];
  let reels = useRef([]);

  const getRandomNr = () => {
    let randomNr = 0;
    if (props.startGame > 0) {
      randomNr = -(Math.floor(Math.random() * 42) * 242);
    }
    console.log("Returning " + randomNr);
    return randomNr;
  };
  const renderReels = () => {
    // let reelEffect= -((Math.floor(Math.random() * 42) + 1)*242);
    console.log("We are spinning the wheel !!!");
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel key={idx} index={idx} gameOn={getRandomNr()}/>;
    });
    console.log("My reelList is: " + reelList.length);
    //  reelEffect=0;
    return <>{reelList}</>;
  };

  useEffect(()=>{
    console.log("my reels are "+reels);
  },[]);

  return (
    <>
      <div className="container">
        <div className="playContainer">
          <div className="reelSet">{renderReels()} ref={reels}</div>
        </div>
      </div>
    </>
  );
};

export default ReelSet;
