import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import {motion} from 'framer-motion';
import Symbol from "./Symbol";

let offset=0;

const Reel = (props) => {
  //42 symbols
//  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPKKQQAAG";
//const symbols = "DDDGNNSS";
 let reels = [];
  const symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";
  let symbolRef = useRef();
  let randomNr= 3;

  const randomBetween = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
  };

  const getRandomNr = () => {
    if (props.game > 0) {
    randomNr = -(randomBetween(
        (Constants.REELS_REPEAT - 2) * symbols[props.index].length,
        (Constants.REELS_REPEAT - 8) * symbols[props.index].length
      ) * 246);
     
    }

    if(offset===randomNr){
        getRandomNr();
    }
   else{
        offset=randomNr;
    }
    console.log("randomNr = "+randomNr);
    generatedSymbols(randomNr);
  };

  if(props.gameOn===0){
      reelSymbols=symbols[props.index].split("");
  }else if(props.gameOn>0){
    reelSymbols= symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
    getRandomNr();
  }  
  
// console.log("My number is: "+(-(props.gameOn/242)));
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))]);
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+1]);
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+2]);

// let getReels="";
const createReels = () =>{
  reels=reelSymbols.map((el, idx) => {
      return (
        <Symbol symbol={el} key={idx} index={idx} spin={props.game} />
      );
    })
    return <>{reels}</>
}

const variants = {
  opacity:{
      opacity: 1 
  },
  spin: {
     y:offset,
     opacity:1, 
     transition:{ 
          duration:3, 
          delay:props.index * 0.8 
      },
  },
  exit : { opacity: 1 }
};

return (
  <motion.div className="reel" >
    <motion.div key={props.index} variants={variants}  opacity="initial"  animate="spin" className="icons" ref={symbolRef}>
      {createReels()}
    </motion.div>
  </motion.div>
);
};
export default Reel;
